  },
  {
    "ayet": 85,
    "sure": 37,
    "text": "Babasına ve halka: \\\"Nelere kulluk ediyorsunuz?\\\" demişti.",
    "text_ar": "إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِۦ مَاذَا تَعْبُدُونَ",
    "text_kelimeler": "",
    "text_okunus": "iẕ ḳâle liebîhi veḳavmihî mâẕâ ta`büdûn."
  },
  {
    "ayet": 86,
    "sure": 37,
    "text": "\\\"Allah'ı bırakarak uydurma ilahlar mı istiyorsunuz?\\\"\"",
    "text_ar": "أَئِفْكًا ءَالِهَةً دُونَ ٱللَّهِ تُرِيدُونَ",
    "text_kelimeler": "",
    "text_okunus": "eifken âliheten dûne-llâhi türîdûn."
  },
  {
    "ayet": 87,
    "sure": 37,
    "text": "\\\"alemlerin Rabb'i hakkında nasıl bir anlayışa sahipsiniz?\\\"\"",
    "text_ar": "فَمَا ظَنُّكُم بِرَبِّ ٱلْعَٰلَمِينَ",
