  },
  {
    "ayet": 56,
    "sure": 53,
    "text": "Bu uyarıcı da ilk uyarıcılar gibi bir uyarıcıdır.",
    "text_ar": "هَٰذَا نَذِيرٌ مِّنَ ٱلنُّذُرِ ٱلْأُولَىٰٓ",
    "text_kelimeler": "",
    "text_okunus": "hâẕâ neẕîrum mine-nnüẕüri-l'ûlâ."
  },
  {
    "ayet": 57,
    "sure": 53,
    "text": "Yaklaşan, yaklaştı.(1)",
    "text_ar": "أَزِفَتِ ٱلْءَازِفَةُ",
    "text_kelimeler": "(1) Kıyamet günü yaklaştıkça yaklaştı.",
    "text_okunus": "ezifeti-l'âzifeh."
  },
  {
    "ayet": 58,
    "sure": 53,
    "text": "Onu, Allah'tan başkası çeviremez.\"",
    "text_ar": "لَيْسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ",
    "text_kelimeler": "",
    "text_okunus": "leyse lehâ min dûni-llâhi kâşifeh."
  },
  {
    "ayet": 59,
    "sure": 53,
    "text": "Bu hadislere(1) mi şaşırıyorsunuz?",
    "text_ar": "أَفَمِنْ هَٰذَا ٱلْحَدِيثِ تَعْجَبُونَ",
    "text_kelimeler": "(1) Sözlere.",
    "text_okunus": "efemin hâẕe-lḥadîŝi ta`cebûn."
  },
  {
    "ayet": 60,
    "sure": 53,
    "text": "Ağlanacak halinize gülüyorsunuz.",
    "text_ar": "وَتَضْحَكُونَ وَلَا تَبْكُونَ",
    "text_kelimeler": "",
