  {
    "ayet": 31,
    "sure": 56,
    "text": "Ve çağlayan sular,",
    "text_ar": "وَمَآءٍ مَّسْكُوبٍ",
    "text_kelimeler": "",
    "text_okunus": "vemâim meskûb."
  },
